    async update(
        id: string,
        updateUserDto: UpdateUserDto,
    ): Promise<StandardOperationResponse> {
        const { avatar, ...updateData } = updateUserDto;
        const dataToUpdate: Partial<User> = { ...updateData };

        // Get user first to know email for cache invalidation
        const existingUser = await this.findOne(id);
        if (!existingUser) {
            throw new NotFoundException(`User with ID ${id} not found`);
        }

        // Convert avatar ID to MediaFile reference if provided
        if (avatar !== undefined) {
            dataToUpdate.avatar = avatar
                ? ({ id: avatar } as MediaFile)
                : undefined;
        }

        await this.userRepository.update(id, dataToUpdate);

        // Invalidate user cache
        await this.invalidateUserCache(id, existingUser.email);
        await this.invalidateUserListCaches();

        return {
            message: 'User updated successfully',
            status: 'success',
            code: 200,
        };
    }

    async remove(id: string): Promise<StandardOperationResponse> {
        const result = await this.userRepository.delete(id);
        if (result.affected === 0) {
            throw new NotFoundException(`User with ID ${id} not found`);
        }

        return {
            message: 'User deleted successfully',
            status: 'success',
            code: 200,
        };
    }

    async updateProfile(
        id: string,
        updateData: Partial<UpdateUserDto>,
    ): Promise<StandardOperationResponse> {
        try {
            const { avatar, ...profileData } = updateData;
            const dataToUpdate: Partial<User> = { ...profileData };

            // Get user first for cache invalidation and event data
            const user = await this.findById(id);
            if (!user) {
                throw new NotFoundException(`User with ID ${id} not found`);
            }

            // Convert avatar ID to MediaFile reference if provided
            if (avatar !== undefined) {
                dataToUpdate.avatar = avatar
                    ? ({ id: avatar } as MediaFile)
                    : undefined;
            }

            await this.userRepository.update(id, dataToUpdate);

            // Invalidate user cache
            await this.invalidateUserCache(id, user.email);

            // Emit user profile updated event
            const updatedFields = Object.keys(updateData);
            this.eventEmitter.emit(
                'user.profile.updated',
                new UserProfileUpdatedEvent(
                    user.id,
                    user.email,
                    user.firstName,
                    user.lastName,
                    user.orgId?.id,
                    user.orgId?.name,
                    user.branchId?.id,
                    user.branchId?.name,
                    user.avatar?.id?.toString(),
                    updatedFields,
                ),
            );

            return {
                message: 'Profile updated successfully',
                status: 'success',
                code: 200,
            };
        } catch (error) {
            if (error instanceof NotFoundException) {
                return {
                    message: error.message,
                    status: 'error',
                    code: 404,
                };
            }
            throw error;
        }
    }

    async changePassword(
        id: string,
        currentPassword: string,
        newPassword: string,
    ): Promise<StandardOperationResponse> {
        const user = await this.findById(id);
        if (!user) {
            return {
                message: `User with ID ${id} not found`,
                status: 'error',
                code: 404,
            };
        }

        // Verify current password
        const isCurrentPasswordValid = await bcrypt.compare(
            currentPassword,
            user.password,
        );
        if (!isCurrentPasswordValid) {
            return {
                message: 'Current password is incorrect',
                status: 'error',
                code: 400,
            };
        }

        // Hash new password
        const saltRounds = 12;
        const hashedNewPassword = await bcrypt.hash(newPassword, saltRounds);

        // Update password
        await this.userRepository.update(id, {
            password: hashedNewPassword,
        });

        // Emit password changed event
        this.eventEmitter.emit(
            'user.password.changed',
            new UserPasswordChangedEvent(
                user.id,
                user.email,
                user.firstName,
                user.lastName,
                user.orgId?.id,
                user.orgId?.name,
                user.branchId?.id,
                user.branchId?.name,
            ),
        );

        return {
            message: 'Password changed successfully',
            status: 'success',
            code: 200,
        };
    }

    /**
     * Assign organization and branch to a user
     */
    async assignOrgAndBranch(
        userId: string,
        orgId?: string,
        branchId?: string,
        assignedBy?: string,
    ): Promise<StandardOperationResponse> {
        return this.retryOperation(async () => {
            const updateData: Record<string, any> = {};

            if (orgId) {
                updateData['orgId'] = { id: orgId };
            }

            if (branchId) {
                updateData['branchId'] = { id: branchId };
            }

            await this.userRepository.update(userId, updateData);

            const user = await this.findOne(userId);
            if (!user) {
                throw new NotFoundException(`User with ID ${userId} not found`);
            }

            // Emit user organization/branch assignment event
            this.eventEmitter.emit(
                'user.org.branch.assigned',
                new UserOrgBranchAssignedEvent(
                    user.id,
                    user.email,
                    user.firstName,
                    user.lastName,
                    user.orgId?.id,
                    user.orgId?.name,
                    user.branchId?.id,
                    user.branchId?.name,
                    user.avatar?.id?.toString(),
                    assignedBy,
                ),
            );

            return {
                message: 'Organization and branch assigned successfully',
                status: 'success',
                code: 200,
            };
        });
    }

