    /**
     * Soft delete a user by setting status to DELETED
     */
    async softDelete(
        userId: string,
        deactivatedBy?: string,
    ): Promise<StandardOperationResponse> {
        return this.retryOperation(async () => {
            // First check if user exists and is not already deleted
            const user = await this.userRepository.findOne({
                where: { id: userId },
                relations: ['orgId', 'branchId', 'avatar'],
            });

            if (!user) {
                throw new NotFoundException(`User with ID ${userId} not found`);
            }

            if (user.status === UserStatus.DELETED) {
                throw new BadRequestException('User is already deleted');
            }

            // Update status to DELETED
            await this.userRepository.update(userId, {
                status: UserStatus.DELETED,
            });

            // Emit user deactivated event
            this.eventEmitter.emit(
                'user.deactivated',
                new UserDeactivatedEvent(
                    user.id,
                    user.email,
                    user.firstName,
                    user.lastName,
                    user.orgId?.id,
                    user.orgId?.name,
                    user.branchId?.id,
                    user.branchId?.name,
                    deactivatedBy,
                    'Account deactivated by user',
                ),
            );

            return {
                message: 'User deleted successfully',
                status: 'success',
                code: 200,
            };
        });
    }

    /**
     * Restore a soft-deleted user by setting status to ACTIVE
     */
    async restoreUser(
        userId: string,
        restoredBy?: string,
    ): Promise<StandardOperationResponse> {
        return this.retryOperation(async () => {
            // First check if user exists and is deleted
            const user = await this.userRepository.findOne({
                where: { id: userId },
                relations: ['orgId', 'branchId', 'avatar'],
            });

            if (!user) {
                throw new NotFoundException(`User with ID ${userId} not found`);
            }

            if (user.status !== UserStatus.DELETED) {
                throw new BadRequestException(
                    'User is not deleted and cannot be restored',
                );
            }

            // Update status to ACTIVE
            await this.userRepository.update(userId, {
                status: UserStatus.ACTIVE,
            });

            // Emit user restored event
            this.eventEmitter.emit(
                'user.restored',
                new UserRestoredEvent(
                    user.id,
                    user.email,
                    user.firstName,
                    user.lastName,
                    user.orgId?.id,
                    user.orgId?.name,
                    user.branchId?.id,
                    user.branchId?.name,
                    restoredBy,
                ),
            );

            return {
                message: 'User restored successfully',
                status: 'success',
                code: 200,
            };
        });
    }

